mapper.select:
SELECT g.g_id, g.g_category_ids, g.g_brand_id, g.g_spu_no, g.g_spu_name, g.g_category_self, g.g_recommend, g.g_unit, g.g_grounding_flag, g.g_create_time, g.g_shop_id, g.g_delete_flag, g.g_status, g.g_update_time, g.g_opinion, g.g_force_opinion, g.g_sku_ids, g.g_goods_id, g.g_sales_num, b.custom_name AS categoryName, ( SELECT min(s.g_price) FROM sps_good_shop_sku s WHERE s.g_gid = g.g_id ) AS gMinPrice, ( SELECT a.album_url FROM sps_goods_album a WHERE a.goods_id = g.g_goods_id AND a.album_type = 0 ORDER BY a.album_id ASC LIMIT 1 ) AS gPic FROM sps_good_shop g LEFT JOIN sps_custom_category b ON b.custom_id = g.g_category_self where 1=1
and g.g_spu_name LIKE CONCAT('%',#{goodsName,jdbcType=VARCHAR},'%')
and g.g_grounding_flag = #{shopStatus,jdbcType=INTEGER}
and g.g_spu_no LIKE CONCAT('%',#{spuNo,jdbcType=VARCHAR},'%')
and g.g_status != 2
and g.g_grounding_flag=2
and g.g_status = #{flowStatus,jdbcType=INTEGER}
and g.g_update_time >= #{startTime}
and g.g_update_time <= #{endTime}
and g.g_shop_id = #{shopNum,jdbcType=VARCHAR}
and g.g_recommend = #{recommend,jdbcType=VARCHAR}
and g.g_category_self = #{categorySelf,jdbcType=INTEGER}
and g.g_delete_flag=0
order by g.g_create_time desc
and g.g_recommend =1 order by g.g_sales_num desc , g.g_create_time desc
order by gMinPrice desc , g.g_create_time desc
order by gMinPrice asc , g.g_create_time desc
order by g.g_sales_num desc , g.g_create_time desc

select count(1)
from sps_good_shop
where 1=1
and g_spu_no = #{gSpuNo,jdbcType=VARCHAR}
 and g_spu_name = #{spuName,jdbcType=VARCHAR}
AND g_delete_flag = 0

SELECT g.g_id, g.g_category_ids, g.g_brand_id, g.g_spu_no, g.g_spu_name, g.g_category_self, g.g_recommend, g.g_unit, g.g_grounding_flag, g.g_create_time, g.g_shop_id, g.g_delete_flag, g.g_status, g.g_update_time, g.g_opinion, g.g_force_opinion, g.g_sku_ids, g.g_goods_id, g.g_sales_num, ce.enterprise_company_name as companyName, co.open_admin_num as adminNum, cc.custom_name as categoryName FROM sps_good_shop g LEFT JOIN sps_channel_enterprise ce ON g.g_shop_id = ce.channel_num LEFT JOIN sps_channel_open_account co ON g.g_shop_id = co.channel_num LEFT JOIN sps_custom_category cc on cc.custom_id=g.g_category_self WHERE 1 = 1
and ce.enterprise_company_name LIKE CONCAT('%',#{shopName,jdbcType=VARCHAR},'%')
and co.open_admin_num LIKE CONCAT('%',#{shopNumber,jdbcType=VARCHAR},'%')
and g.g_spu_name LIKE CONCAT('%',#{goodsName,jdbcType=VARCHAR},'%')
and g.g_status = #{shopStatus,jdbcType=INTEGER}
and g.g_update_time >= #{startTime}
and g.g_update_time <= #{endTime}
g.g_shop_id = #{gShopId,jdbcType=VARCHAR},
AND g.g_grounding_flag != 0 AND g.g_grounding_flag != 2 AND g.g_status != 0 AND g.g_delete_flag=0 ORDER BY g.g_create_time DESC

SELECT g.g_id, g.g_category_ids, g.g_brand_id, g.g_spu_no, g.g_spu_name, g.g_category_self, g.g_recommend, g.g_unit, g.g_grounding_flag, g.g_create_time, g.g_shop_id, g.g_delete_flag, g.g_status, g.g_update_time, g.g_opinion, g.g_force_opinion, g.g_sku_ids, g.g_goods_id, g.g_sales_num, ce.enterprise_company_name as companyName, co.open_admin_num as adminNum, cc.custom_name as categoryName FROM sps_good_shop g LEFT JOIN sps_channel_enterprise ce ON g.g_shop_id = ce.channel_num LEFT JOIN sps_channel_open_account co ON g.g_shop_id = co.channel_num LEFT JOIN sps_custom_category cc on cc.custom_id=g.g_category_self WHERE 1 = 1
and ce.enterprise_company_name LIKE CONCAT('%',#{shopName,jdbcType=VARCHAR},'%')
and co.open_admin_num LIKE CONCAT('%',#{shopNumber,jdbcType=VARCHAR},'%')
and g.g_spu_name LIKE CONCAT('%',#{goodsName,jdbcType=VARCHAR},'%')
and g.g_grounding_flag = #{shopStatus,jdbcType=INTEGER}
and g.g_update_time >= #{startTime}
and g.g_update_time <= #{endTime}
g.g_shop_id = #{gShopId,jdbcType=VARCHAR},
and g.g_spu_no LIKE CONCAT('%',#{spuNo,jdbcType=VARCHAR},'%')
and g.g_status = #{status,jdbcType=INTEGER}
AND g.g_delete_flag=0 ORDER BY g.g_update_time DESC
