mapper.select:
select
from sps_good_shop_sku where 1=1
and g_gid = #{goodsId,jdbcType=INTEGER}

select s.g_id, s.g_gid, s.g_color, s.g_size, s.g_sku, s.g_price, s.g_quantity, s.g_stock, s.g_scale, s.g_service,s.g_goods_id, s.g_delete_flag, s.g_sku_id, s.g_create_time, s.g_update_time,g.g_spu_name as spuName from sps_good_shop_sku s LEFT JOIN sps_good_shop g on g.g_id= s.g_gid where 1=1
and (s.g_size LIKE CONCAT('%',#{spec,jdbcType=VARCHAR},'%')or s.g_color LIKE CONCAT('%',#{spec,jdbcType=VARCHAR},'%'))
and s.g_sku LIKE CONCAT('%',#{goodSku,jdbcType=VARCHAR},'%')
and g.g_spu_name LIKE CONCAT('%',#{goodsName,jdbcType=VARCHAR},'%')
and s.g_update_time >= #{startTime}
and s.g_update_time<= #{endTime}
and g.g_shop_id = #{shopNum,jdbcType=VARCHAR}
and g.g_status=2 and g.g_grounding_flag!=2 AND s.g_delete_flag=0 ORDER BY s.g_create_time DESC
