mapper.select:
SELECT x.id,x.bt_trade_amount,x.bt_trade_status,x.bt_trade_type,x.bt_application_start_date FROM ( SELECT DATE_FORMAT( bt.bt_application_start_date, "%Y-%m-%d " ) a , bt.id, bt.bt_user_id userId FROM sps_bank_trade_info bt GROUP BY DATE_FORMAT( bt.bt_application_start_date, "%Y-%m-%d %H" ) ) b JOIN sps_bank_trade_info x ON b.a =DATE_FORMAT( x.bt_application_start_date, "%Y-%m-%d " )
x.bt_user_id=#{userId}
and bt_trade_type=#{tradeType}
order by b.a DESC

SELECT tr.id, tr.bt_trade_amount, tr.bt_trade_status, tr.bt_trade_type, tr.bt_application_start_date, tr.recharge_status, CONCAT(REPLACE(DATE_FORMAT( tr.bt_application_start_date,"%Y-%m"),'-','年'),'月') AS dateDesc FROM sps_bank_trade_info tr WHERE tr.bt_trade_name =#{constomeId}
and bt_trade_type = #{types}
and bt_trade_type = #{types}
and bt_trade_type IN(0,1)
ORDER BY tr.bt_application_start_date DESC limit #{currentPage},#{pageSize}
