mapper.select:
  	SELECT

		sps_bank_trade_info bt 
	join 
	sps_bank  b
	on 
	bt.bt_user_id = b.b_user_id

		bt.bt_trade_type = #{tradeType,jdbcType=VARCHAR}
		and  b.channl_num=#{channelNum,jdbcType=VARCHAR}

			and bt.bt_application_start_date   >=     #{applicationStartDate,jdbcType=VARCHAR}


			and bt.bt_application_start_date   <=     #{paymentDate,jdbcType=VARCHAR}



			and bt.bt_trade_status = #{tradeStatus}


	  ORDER  BY  bt.bt_application_start_date  desc

SELECT
sps_bank_trade_info bt join sps_bank b on bt.bt_user_id = b.b_user_id
bt.bt_trade_serial_num=#{tradeSerialNum}
and b.channl_num=#{channelNum}

SELECT
sps_bank_trade_info bt join sps_bank b on bt.bt_user_id= b.b_user_id JOIN sps_channel_enterprise e ON e.channel_num=b.channl_num
bt.bt_trade_type="0"
and bt.bt_application_start_date >= #{applicationStartDate,jdbcType=VARCHAR}
and bt.bt_application_start_date <= #{paymentDate,jdbcType=VARCHAR}
and bt.bt_trade_status = #{tradeStatus,jdbcType=VARCHAR}
and b.b_user_name like CONCAT('%',#{loginName,jdbcType=VARCHAR},'%')
and e.enterprise_company_name like CONCAT('%',#{enterpriseCompanyName,jdbcType=VARCHAR},'%')
ORDER BY bt.bt_application_start_date desc

SELECT
sps_bank_trade_info bt join sps_bank b on bt.bt_user_id= b.b_user_id JOIN sps_channel_enterprise e ON e.channel_num=b.channl_num
and bt.bt_payment_date >= #{startTime,jdbcType=VARCHAR}
and bt.bt_payment_date <= #{endTime,jdbcType=VARCHAR}
and bt.bt_trade_amount >= #{minAmount,jdbcType=VARCHAR}
and bt.bt_trade_amount <= #{maxAmount,jdbcType=VARCHAR}
and bt.bt_trade_type = #{payType,jdbcType=VARCHAR}
and e.enterprise_company_name like CONCAT('%',#{companyName,jdbcType=VARCHAR},'%')
and bt.standby1 like CONCAT('%',#{reamrk,jdbcType=VARCHAR},'%')
ORDER BY bt.bt_payment_date desc

SELECT
sps_bank_trade_info bt join sps_bank b on bt.bt_user_id= b.b_user_id JOIN sps_channel_enterprise e ON e.channel_num=b.channl_num
bt.bt_trade_type = #{payType,jdbcType=VARCHAR}
and bt.bt_audit_date >= #{startTime,jdbcType=VARCHAR}
and bt.bt_audit_date <= #{endTime,jdbcType=VARCHAR}
and bt.bt_trade_amount >= #{minAmount,jdbcType=VARCHAR}
and bt.bt_trade_amount <= #{maxAmount,jdbcType=VARCHAR}
and e.enterprise_company_name like CONCAT('%',#{companyName,jdbcType=VARCHAR},'%')
and bt.standby1 like CONCAT('%',#{reamrk,jdbcType=VARCHAR},'%')
ORDER BY bt.bt_payment_date desc

SELECT

sps_bank_trade_info bt
join
sps_bank  b
on
bt.bt_user_id= b.b_user_id
JOIN
sps_channel_enterprise e
ON
e.channel_num=b.channl_num

	  b.channl_num=#{channelNum,jdbcType=VARCHAR}

		and bt.bt_audit_date   >=     #{startTime,jdbcType=VARCHAR}


		and bt.bt_audit_date   <=     #{endTime,jdbcType=VARCHAR}


		and bt.bt_trade_type = #{payType,jdbcType=VARCHAR}



		and bt.bt_trade_amount   >=   #{minAmount,jdbcType=VARCHAR}


		and bt.bt_trade_amount  <=   #{maxAmount,jdbcType=VARCHAR}


		and e.enterprise_company_name like CONCAT('%',#{companyName,jdbcType=VARCHAR},'%')


		and bt.standby1 like CONCAT('%',#{reamrk,jdbcType=VARCHAR},'%')


ORDER  BY   bt.bt_payment_date  desc
